import React, { Component } from 'react';
class BackgroundCanvas extends Component {

    constructor (props){
        super(props);
        this.canvasRef = React.createRef();
    }

    loadImage(image, size){
        const canvas = this.canvasRef.current;
        const context = canvas.getContext('2d');
        const img = new Image();
        img.src = image.url; 
        img.name = image.name;
        img.onload = () => {
            context.canvas.width = size[0];
            context.canvas.height = size[1];
            context.drawImage(img, 0, 0);
        }
    }


    render() {
        if (this.props.image.name!==undefined) {
            const { image, size } = this.props;
            this.loadImage(image, size);
        } 

        return(
            <canvas id="background" ref={this.canvasRef} style={{ zIndex: 1, position:'absolute', backgroundColor:'red'}}>
                Sorry, your browser doesn't support the &lt;canvas&gt; element.
            </canvas>
        )

    }

}
export default BackgroundCanvas;
